import styled from '@emotion/styled';
import {contact, menu /*slides, heading, about, newsletter, connect, blog, welcome, portfolio*/} from '../data/app-content';
import Sidebar from './routes/sidebar';
import FooterIcons from '../components/footer-icons';
import Footer from './routes/footer';
import ActionBlock from '../components/action-block';

const Container = styled.div`
  margin-right: auto;
  margin-left: auto;
  max-width: 1200px;
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: auto;
  grid-template-areas: unset;
  gap: 0;
  @media (min-width: 640px) {
    grid-template-columns: 170px 1fr;
    gap: 30px;
    grid-template-areas:
      'sidebar header'
      'sidebar main';
  }
`;

const Layout = props => (
    <>
        <Container>
            <Sidebar img menu={menu} />
            {props.children}
        </Container>
        <Footer subtext={`© WOMENS MINISTRY IN THE PRESBYTERIAN CHURCH OF AUSTRALIA ${new Date().getFullYear()}. IN PARTNERSHIP WITH NEW FRONT DOOR`}>
            <FooterBox area="sidebar">
            <div>
                {contact.content[1].map(item => (
                <ActionBlock
                    heading={item.heading}
                    content={item.content}
                    action={item.action}
                    url={item.url}
                    key={item.heading}
                />
                ))}
            </div>
            </FooterBox>
            <FooterIcons
            
            /*twitter={{url: 'test'}}
            instagram={{url: 'test'}}
            podcast={{url: 'test'}}
                login={{url: 'test'}}*/

            facebook={{url: 'https://www.facebook.com/wmpres/'}}
            />
            <FooterBox area="sidebar2">
            {/*<LatestSermon
                title="Test"
                preacher="Test Guy"
                sermonUrl="www.google.com"
            />*/}
            </FooterBox>
            <FooterBox area="tertiary">
            <p></p>
            </FooterBox>
        </Footer>
      </>
);

export default Layout;
